package programmers.test.n;



public class Solution7_2 {
    public static void main(String[] args) {
//        String S = "011100"; // 7
//        String S = "111"; //5
        String S = "1111010101111"; //22
//        String S = ""; //22
        // 79999
        System.out.println(solution(S));
    }

    public static int solution(String s) {
        int count = 0;
        // 처음 시작이 0이 아닌 경우 제거, 이거빼고는 다 되는 듯
        while (!s.equals("0") && !s.equals("00")) {
            int i = s.length() - 1;
            char c = s.charAt(i);

            if (c == '1') {
                c = '0';
                s = s.substring(0, i);
                s += c;
                count++;
            } else {
                s = s.substring(0, i);
                count++;
            }
        }

        return count;
    }


}
